const faviconHandler=()=>{const e=document.querySelector('link[rel="icon"][type="image/x-icon"]');if(!e)return void console.warn("Favicon link element not found.");const n=e.getAttribute("href"),t=document.hidden?n.replace(/(\.[a-z]{2,4})$/,"-inactive$1"):n.replace(/-inactive(\.[a-z]{2,4})$/,"$1");e.setAttribute("href",t)};"loading"===document.readyState?document.addEventListener("DOMContentLoaded",document.addEventListener("visibilitychange",faviconHandler)):document.addEventListener("visibilitychange",faviconHandler);